// Generated by LiveScript 1.2.0
var app;
app = new Vue({
  el: '#todoapp',
  data: {
    title: '',
    activeFilter: 'all',
    all: JSON.parse(localStorage.getItem('v')) || [{
      title: 'Golfing',
      completed: true,
      oldTitle: false
    }]
  },
  ready: function(){
    return this.$watch('all', function(it){
      return localStorage.v = JSON.stringify(it);
    }, {
      deep: true
    });
  },
  computed: {
    active: function(){
      return _.reject(function(it){
        return it.completed;
      })(
      this.all);
    },
    completed: function(){
      return _.filter(function(it){
        return it.completed;
      })(
      this.all);
    },
    allDone: {
      get: function(){
        return this.active.length === 0;
      },
      set: function(v){
        return _.each(function(it){
          return it.completed = v;
        })(
        this.all);
      }
    }
  },
  methods: {
    create: function(){
      var that;
      if (that = this.title.trim()) {
        this.all.push({
          title: that,
          completed: false,
          oldTitle: false
        });
        return this.title = '';
      }
    },
    cancel: function(it){
      it.title = it.oldTitle;
      return it.oldTitle = '';
    },
    save: function(it){
      it.oldTitle = '';
      if (!it.title.trim()) {
        return this.all.$remove(it);
      }
    },
    removeCompleted: function(){
      return this.all = _.reject(function(it){
        return it.completed;
      })(this.all);
    }
  }
});
Router({
  '*': function(it){
    return app.activeFilter = it;
  }
}).init('/all');